import React, { } from 'react';
import { useParams, RouteComponentProps } from 'react-router-dom';
import styled from 'styled-components';
import useGetHandler from '../hooks/FlaskAPI';
import {Spin, message, Layout, Divider, Affix, Button} from 'antd'
import {EntryWhole} from '../data/Entry'

const slugRequest = {
  method: 'get',
  url: '',
};

const SplashContainer = styled.div `
  display: flex;
  justify-content: center;
  align-items: center;

  height: 20vw;
  width: 100%;
`
const LeftFlex = styled.div `
  display: flex;
  justify-content: left;
  align-items: center;
`

const Timestamp = styled.h5 `
  margin: 10px 20px;
  color: lightgray;
`

const ContentContainer = styled.div `
  margin-left: 20%;
  margin-right: 20%;
  margin-top: 20px;
  margin-bottom: 20px;
`
const Content = styled.div `
  color: black;
  size: large;
`
export const EntryMain: React.FC<RouteComponentProps<{slug: string}>> = (props: any) => {
  const slug = props.match.params.slug
  const { data, isLoading, hasError, handleRequest } = useGetHandler();
  console.log(slug)

  if (hasError){
    message.error("An error has occured. Please reload");

    return(
      <div>
        <SplashContainer/>
        <Spin/>
      </div>
    )
  }

  if (data === null && isLoading === false){
    slugRequest.url = "/"+slug+"/"
    console.log(slugRequest)
    handleRequest(slugRequest)
  }


  return (
    (data === null || isLoading) ?
    <div>
      <SplashContainer/>
      <Spin/>
    </div>
      :
    <div>
      <SplashContainer style={{backgroundSize:"cover", backgroundImage:"url("+data[0].photoLink+")"}}>
        <h2 style={{color:"white", textShadow:"rgb(0, 0, 0) 3px 0px 0px, rgb(0, 0, 0) 2.83487px 0.981584px 0px, rgb(0, 0, 0) 2.35766px 1.85511px 0px, rgb(0, 0, 0) 1.62091px 2.52441px 0px, rgb(0, 0, 0) 0.705713px 2.91581px 0px, rgb(0, 0, 0) -0.287171px 2.98622px 0px, rgb(0, 0, 0) -1.24844px 2.72789px 0px, rgb(0, 0, 0) -2.07227px 2.16926px 0px, rgb(0, 0, 0) -2.66798px 1.37182px 0px, rgb(0, 0, 0) -2.96998px 0.42336px 0px, rgb(0, 0, 0) -2.94502px -0.571704px 0px, rgb(0, 0, 0) -2.59586px -1.50383px 0px, rgb(0, 0, 0) -1.96093px -2.27041px 0px, rgb(0, 0, 0) -1.11013px -2.78704px 0px, rgb(0, 0, 0) -0.137119px -2.99686px 0px, rgb(0, 0, 0) 0.850987px -2.87677px 0px, rgb(0, 0, 0) 1.74541px -2.43999px 0px, rgb(0, 0, 0) 2.44769px -1.73459px 0px, rgb(0, 0, 0) 2.88051px -0.838247px 0px"}}>
          {data[0].title}</h2>
      </SplashContainer>
      <Divider orientation="left">{data[0].timestamp}</Divider>
      <ContentContainer>
        <Content>{data[0].content}</Content>
      </ContentContainer>
    </div>
  )
}
/*
      <LeftFlex>
        <Timestamp>{data[0].timestamp}</Timestamp>
      </LeftFlex>

*/

export default EntryMain;
