import * as React from 'react';
import styled from 'styled-components';
import { ArticleMeta } from '../data/ArticleMeta'
import { Typography } from 'antd'

const {Title} = Typography;


const Layout = styled.div`
  width: 90% auto;
  margin: 16px auto;
  padding: 16px;
  position: relative;
  min-width:280;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
`;

const ArticleFlex = styled.div `
  margin: 10px;
  flex-grow: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ArticleContainer = styled.div `
  height: 200px;
  width: 200px;

  background-image: url('placeholder.jpg');

  display: flex;
  justify-content: center;
  align-items: center;
`

const Article: React.FC<{
  entry: ArticleMeta
}> = (props) => {
  return (
    <ArticleFlex >
      <ArticleContainer background-image="url(placeholder.jpg)">
        <div ><Title level={3}>{props.entry.title}</Title></div>
      </ArticleContainer>
    </ArticleFlex>
  )
}

const ArticleSummary: React.FC<{
  entries: ArticleMeta[]
}> = (props) => {
  return (
    <div style={{backgroundColor:"white"}}>
      <h1 >-- Dive Into My Brain --</h1>
      <Layout>
        {props.entries.map(e => <Article entry={e} />)}
      </Layout>
    </div>
  );
};

export default ArticleSummary;
