import React, { } from 'react';
import { useParams, RouteComponentProps } from 'react-router-dom';
import styled from 'styled-components';
import useGetHandler from '../hooks/FlaskAPI';
import {Spin, message, Layout} from 'antd'

const slugRequest = {
  method: 'get',
  url: '',
};

const SplashContainer = styled.div `
  display: flex;
  justify-content: center;
  align-items: center;
  background: blue;

  height: 20vw;
  width: 100%;
`
export const EntryMain: React.FC<RouteComponentProps<{slug: string}>> = (props: any) => {
  const slug = props.match.params.slug
  const { data, isLoading, hasError, handleRequest } = useGetHandler();
  console.log(slug)

  if (hasError){
    message.error("An error has occured. Please reload");

    return(
      <div>
        <SplashContainer/>
        <Spin/>
      </div>
    )
  }

  if (data === null && isLoading === false){
    slugRequest.url = "/"+slug+"/"
    console.log(slugRequest)
    handleRequest(slugRequest)
  }

  return (
    (data !== null ) ?
    <div>
      <SplashContainer>
        <h1>
          {data.title}
        </h1>
      </SplashContainer>
      <h5>{data.published}</h5>
    </div>
      :
    <div>
      <SplashContainer/>
      <Spin/>
    </div>
  )
}

export default EntryMain;
