import React, { useState } from 'react';
import styled from 'styled-components';
import { ArticleMeta } from '../data/ArticleMeta'
import { message, Layout, Space, Carousel, Card, Typography, Divider } from 'antd'
import { ArrowLeftOutlined, FileTextOutlined, GithubOutlined } from '@ant-design/icons'

const { Header, Footer, Sider, Content } = Layout;
const { Title } = Typography;

export const ProjectsCarousel: React.FC<{
  entries: ArticleMeta[]
}> = (props: any) => {
  const CenterFlex = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
  `
  return (
    <CenterFlex>
      <div style={{ width: "100%" }}>
        <Carousel autoplay lazyLoad="ondemand" autoplaySpeed={4000} speed={800} >

          {props.entries.map((e) =>
            <TerminalCard entry={e} />
          )}
        </Carousel>
      </div>
    </CenterFlex>
  )
}

const TerminalCard: React.FC<{
  entry: ArticleMeta
}> = (props: any) => {
  const LeftFlex = styled.div`
    display: flex;
    justify-content: left;
    align-items: center;
  `

  return (
    <div style={{ backgroundColor: "white" }}>
      <LeftFlex>
        <Terminal entry={props.entry}></Terminal>
        <div style={{ flexBasis: "100%", alignSelf: "flex-start", wordWrap: "break-word" }}>
          <Title level={2} style={{ textAlign: "left", margin: "10px" }}>{props.entry.title}</Title>
          <div>
            <p style={{ lineHeight: "30px", textAlign: "left", margin: "10px" }}> jshdkafjhjksh kjdfh kh sakd hkj as khjdhj kask jhkhj dfj khjhsk jdhfkajh sdfkjhh sajkh dfjkh askjhdfkj haskj d kjh aksdkjh fh akjshdjkf has hdh fakjshdfjkf akjsh fdh {props.entry.summary}</p>
          </div>
        </div>
      </LeftFlex>
      <div style={{ height: "60px" }} />
    </div>
  )
}

enum Display {
  Live,
  Code,
}

const Terminal: React.FC<{
  entry: ArticleMeta
}> = (props: any) => {
  const [display, setDisplay] = useState(Display.Live)

  if (!('code' in props.entry)) {
    message.error("Entry does not have code")
    return (<div>ERROR</div>)
  }

  const TerminalContainer = styled.div`
    height: auto;
    width: auto;
    border: solid;
    border-width: 2px;

  `
  const TerminalTop = styled.div`
    height: 20px;
    width: auto;
    background-color: #2E3463;
    color: #C0BEBF;

    display: flex;
    justify-content: center;
    align-items: center;
  `
  return (
    <div>
      <TerminalContainer>
        <TerminalTop>Creator-79@pop-os</TerminalTop>
        {display === Display.Live ?
          <img src={require("../static/giffy.gif")} alt="This will display an animated GIF" style={{ maxWidth: "100%", maxHeight: "100%" }} />
          :
          <Code code="Hello" />
        }
      </TerminalContainer>
      <div style={{ border: "solid", borderWidth: "1px", borderColor: "grey", lineHeight: "20px", textAlign: "right", height: "60px", backgroundColor: "white" }}>
        <GithubOutlined style={{ fontSize: "40px", margin: "10px 10px" }} onClick={(e) => setDisplay(Display.Code)} />
        {(display === Display.Live) ?
          <FileTextOutlined style={{ fontSize: "40px", margin: "10px 10px" }} onClick={(e) => setDisplay(Display.Code)} />
          :
          <ArrowLeftOutlined style={{ fontSize: "40px", margin: "10px 10px"}} onClick={(e) => setDisplay(Display.Live)} />
        }
      </div>
    </div>
  )
}

const Code: React.FC<{
  code: string
}> = (props: any) => {
  return (<div style={{ height: "307px", width: "528px" }}>{props.code}</div>)
}




export default ProjectsCarousel
