import React, { useState } from 'react';
import styled from 'styled-components';
import { EntryTrunc } from '../data/Entry'
import { message, Layout, Space, Carousel } from 'antd'

const { Header, Footer, Sider, Content } = Layout;

export const TerminalDisplay: React.FC<{
  entries: EntryTrunc[]
}> = (props: any) => {
  return (
    <CenterFlex>
      <div style={{ width: "80%" }}>
        <Carousel autoplay>
          {props.entries.map((e) =>
            <TerminalCard entry={e} />
          )}
        </Carousel>
      </div>
    </CenterFlex>
  )
}

const TerminalCard: React.FC<{
  entry: EntryTrunc
}> = (props: any) => {
  return (
    <Layout>
      <Layout>
        <Sider width="800" style={{ background: "white" }}><Terminal entry={props.entry} /></Sider>
        <Layout>
          <Header style={{ background: "grey" }}>Header</Header>
          <Content>Content</Content>
        </Layout>
      </Layout>
      <Footer>Footer</Footer>
    </Layout>
  )
}


const Terminal: React.FC<{
  entry: EntryTrunc
}> = (props: any) => {
  if (!('code' in props.entry)) {
    message.error("Entry does not have code")
    return (<div>ERROR</div>)
  }
  return (
    <TerminalContainer>
      <TerminalTop>Creator-79@pop-os</TerminalTop>
    </TerminalContainer>
  )
}
const CenterFlex = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`

const TerminalTop = styled.div`
  height: 20px;
  width: auto;
  background-color: #2E3463;
  color: #C0BEBF;

  display: flex;
  justify-content: center;
  align-items: center;
`
const TerminalContainer = styled.div`
  height: 400px;
  width: auto;
  border: solid;
  border-radius: 1px;

`

export default TerminalDisplay
