import React, { useState } from 'react';
import styled from 'styled-components';
import { EntryTrunc } from '../data/Entry'
import { message, Layout, Space, Carousel, Card, Typography, Row, Col } from 'antd'
import { CodeOutlined, RightSquareOutlined } from '@ant-design/icons'

const { Header, Footer, Sider, Content } = Layout;
const { Title } = Typography;

export const TerminalCarousel: React.FC<{
  entries: EntryTrunc[]
}> = (props: any) => {
  const CenterFlex = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
  `
  return (
    <CenterFlex>
      <div style={{ width: "85%" }}>
        <Carousel autoplay>
          {props.entries.map((e) =>
            <TerminalCard entry={e} />
          )}
        </Carousel>
      </div>
    </CenterFlex>
  )
}

const TerminalCard: React.FC<{
  entry: EntryTrunc
}> = (props: any) => {

  return (
    <Row>
      <Col flex="400px"><Terminal entry={props.entry} /></Col>
      <Col flex="auto">
          <Title level={2} >{props.entry.title}</Title>
      </Col>
    </Row>
  )
}

enum Display {
  Live,
  Code,
}

const Terminal: React.FC<{
  entry: EntryTrunc
}> = (props: any) => {
  const [display, setDisplay] = useState(Display.Live)

  if (!('code' in props.entry)) {
    message.error("Entry does not have code")
    return (<div>ERROR</div>)
  }

  const TerminalContainer = styled.div`
    height: auto;
    width: auto;
    border: solid;
    border-width: 2px;

  `
  const TerminalTop = styled.div`
    height: 20px;
    width: auto;
    background-color: #2E3463;
    color: #C0BEBF;

    display: flex;
    justify-content: center;
    align-items: center;
  `
  return (
    <div>
      <TerminalContainer>
        <TerminalTop>Creator-79@pop-os</TerminalTop>
        {display === Display.Live ?
          <img src={require("../static/giffy.gif")} alt="This will display an animated GIF" style={{ maxWidth: "100%", maxHeight: "100%" }} />
          :
          <Code code="Hello" />
        }

        <div style={{ textAlign: "right", height: "100px" }}>
          <CodeOutlined style={{ fontSize: "20px" }} onClick={(e) => setDisplay(Display.Code)} />
          <RightSquareOutlined style={{ fontSize: "20px" }} onClick={(e) => setDisplay(Display.Live)} />
        </div>
      </TerminalContainer>
    </div>
  )
}

const Code: React.FC<{
  code: string
}> = (props: any) => {
  return (<div>{props.code}</div>)
}




export default TerminalCarousel
