import * as React from 'react';
import styled from 'styled-components';
import EntryTrunc from '../data/Entry'


const Layout = styled.div`
  width: 90% auto;
  margin: 16px auto;
  padding: 16px;
  position: relative;
  min-width:280;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;

`;

const DescripionContainer = styled.div `
  width: 20px;
  margin: 20px;
  flex-grow: 1;
`;

const Article: React.FC<{
  entry: EntryTrunc
}> = (props) => {
  return (
    <DescripionContainer style={{backgroundImage:"placeholder.jpg"}}>
      <h3>Hello</h3>
    </DescripionContainer>
  )
}

const ArticleSummary: React.FC<{
  entries: EntryTrunc[]
}> = (props) => {
  return (
    <Layout>
      {props.entries.map(e => <Article entry={e} />)}
    </Layout>
  );
};

export default ArticleSummary;
