import * as React from 'react';
import styled from 'styled-components';

//margin: 30px;
const Layout = styled.div`
  width: 90% auto;
  margin: 16px auto;
  padding: 16px;
  position: relative;
  min-width:280;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;

`;

const DescripionContainer = styled.div `
  width: 20px;
  margin: 20px;
  flex-grow: 1;
  text-align: center;
`;

export const Count: React.FunctionComponent<{
  title: string,
  published: string,
  summary: string,
  resource: string
}> = (props) => {


  return (
    <div>
      <Layout>
          <img src="placeholder.jpg" alt="Project descriptio" style={{height:"400px", maxHeight:"60%", minHeight:"20%", flexGrow:1, marginLeft:"20px"}}/>
        <DescripionContainer style={{backgroundImage:"./static/placeholder.jpg"}}>
          <h3>Hello</h3>
        </DescripionContainer>
      </Layout>
    </div>
  );
};

export default Count;
