import * as React from 'react';
import styled from 'styled-components';
import EntryTrunc from '../data/Entry'


const Layout = styled.div`
  width: 90% auto;
  margin: 16px auto;
  padding: 16px;
  position: relative;
  min-width:280;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;

`;

const ArticleFlex = styled.div `
  margin: 10px;
  flex-grow: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ArticleContainer = styled.div `
  height: 100px;
  width: 100px;
  background-image: url('placeholder.jpg');
`

const Article: React.FC<{
  entry: EntryTrunc
}> = (props) => {
  return (
    <ArticleFlex >
      <ArticleContainer>
        <h3>Hello</h3>
      </ArticleContainer>
    </ArticleFlex>
  )
}

const ArticleSummary: React.FC<{
  entries: EntryTrunc[]
}> = (props) => {
  return (
    <Layout>
      {props.entries.map(e => <Article entry={e} />)}
    </Layout>
  );
};

export default ArticleSummary;
