import React, { useState } from 'react';
import styled from 'styled-components';
import EntryTrunc from '../data/Entry'
import { useParams, RouteComponentProps } from 'react-router-dom';
import { createGlobalState } from 'react-hooks-global-state';
import useGetHandler from '../hooks/FlaskAPI'
import ProjectSummary from './ProjectSummary'
import queryString from 'query-string'
import {Spin, message, Space, Layout} from 'antd'


const initialState = { entries: null, lastSearch: '' }
const { useGlobalState } = createGlobalState(initialState)

const articlesRequest = {
  method: 'get',
  url: 'articles/',
};

interface rquery {
  id?: string
}

//const projectEntries = [
  //{title:"hello1", published:"1/1/1", summary:"this is my summary", resource:"www.test"} as EntryTrunc,
  //{title:"hello2", published:"1/1/1", summary:"this is my summary", resource:"www.test"} as EntryTrunc,
  //{title:"hello3", published:"1/1/1", summary:"this is my summary", resource:"www.test"} as EntryTrunc,
//]
                  //<ProjectSummary entries={projectEntries}/>

export const BlogMain: React.FC<RouteComponentProps<rquery>> = (props: any) => {

  const [entries, setEntries] = useGlobalState('entries');
  const [lastSearch, setLastSearch] = useGlobalState('lastSearch');
  const { data, isLoading, hasError, handleRequest } = useGetHandler();

  //console.log(JSON.stringify(props))
  let paramsSearch = props.location.search
  const params = (paramsSearch.length !== 0) ?
    queryString.parse(paramsSearch.substr(1))
  :
    {}
  console.log(params)

  if (hasError) {
    message.error("An error has occured, please reload the page")
    return <div>Error</div>
  }

  if (data !== entries) {
    console.log("SET")
    setEntries(data)
  }

  if (isLoading) {
    return (<Spin size='large'/>)
  }
  else {
    // Display without search query
    if (!('q' in params)) {
      if (lastSearch === "" && entries !== null) {
        return (
          <div>
            <CenterFlex><h1 style={{marginTop:"20px"}}>Hello!</h1></CenterFlex>
            <EntryList entries={entries as unknown as EntryTrunc[]}/>
          </div>
        );
      }
      else {
        handleRequest(articlesRequest)
      }
    }
    // Display using search query
    else if (params.q === lastSearch) {
      return <div>Entries search={params}</div>
    }
    else {
      handleRequest(articlesRequest)
    }
  }

  return (<div></div>)
};


const CenterFlex = styled.div `
  display: flex;
  justify-content: center;
  align-items: center;
`
const EntryList: React.FC<{
  entries: EntryTrunc[]
}> = (props) => {

  return (
    <div style={{margin:"10px"}}>
      <CenterFlex>
          {props.entries.map((e) => <ul><Entry entry={e}/></ul>)}
      </CenterFlex>
    </div>
  )
}

const Thumbnail = styled.img `
  height: auto;
  width: auto;
  border-radius: 50%;
  background-size: cover;

`

const {Header, Footer, Sider, Content} = Layout;
const Entry: React.FC<{
  entry: EntryTrunc
}> = (props) => {

  return (
    <div style={{width:"70vw"}}>
      <Layout>
        <Sider style={{background:"white"}}>
          <Thumbnail src="github.png"/>
        </Sider>
        <Layout>
          <Header style={{background:"#f0f2f5"}}><h1 style={{color:'blue', textAlign:"left"}}>{props.entry.title}</h1></Header>
          <Footer><h6>published ehenver</h6></Footer>
          {/*<Content style={{background:"#f0f2f5"}}><h2 style={{color:'blue', textAlign:"left", marginLeft:"4%"}}>{props.entry.summary}</h2></Content>*/}
        </Layout>
      </Layout>
    </div>
  );
}

//{props.entries.map(e => <Project entry={e} />)}

export default BlogMain;
