import React, { } from 'react';
import { useParams, RouteComponentProps } from 'react-router-dom';
import styled from 'styled-components';
import useGetHandler from '../hooks/FlaskAPI';
import {Spin, message, Layout} from 'antd'
import {EntryWhole} from '../data/Entry'

const slugRequest = {
  method: 'get',
  url: '',
};

const SplashContainer = styled.div `
  display: flex;
  justify-content: center;
  align-items: center;
  background: blue;

  height: 20vw;
  width: 100%;
`
const PublishedDate = styled.div `
  color: lightcoral;
  display: flex;
  justify-content: left;
  align-items: center;
`
const ContentContainer = styled.div `
  margin: 10px, 20%;
  display: flex;
`
const Content = styled.div `
  color: blanchedalmond;
  size: large;
`
export const EntryMain: React.FC<RouteComponentProps<{slug: string}>> = (props: any) => {
  const slug = props.match.params.slug
  const { data, isLoading, hasError, handleRequest } = useGetHandler();
  console.log(slug)

  if (hasError){
    message.error("An error has occured. Please reload");

    return(
      <div>
        <SplashContainer/>
        <Spin/>
      </div>
    )
  }

  if (data === null && isLoading === false){
    slugRequest.url = "/"+slug+"/"
    console.log(slugRequest)
    handleRequest(slugRequest)
  }


  return (
    (data === null || isLoading) ?
    <div>
      <SplashContainer/>
      <Spin/>
    </div>
      :
    <div>
      <SplashContainer><h1>{data[0].title}</h1></SplashContainer>
      <PublishedDate><h5 style={{margin:"10px 20px"}}>{data[0].published}</h5></PublishedDate>
      <ContentContainer>
        <Content>{data[0].summary}</Content>
      </ContentContainer>
    </div>
  )
}

export default EntryMain;
