import React, { } from 'react';
import { useParams, RouteComponentProps } from 'react-router-dom';
import styled from 'styled-components';
import useGetHandler from '../hooks/FlaskAPI';
import {Spin, message, Typography, Divider, Affix, Button} from 'antd'
import {EntryWhole} from '../data/Entry'

const {Title} = Typography

const slugRequest = {
  method: 'get',
  url: '',
};

const SplashContainer = styled.div `
  display: flex;
  justify-content: center;
  align-items: center;

  height: 20vw;
  width: 100%;
`
const LeftFlex = styled.div `
  display: flex;
  justify-content: left;
  align-items: center;
`

const Timestamp = styled.h5 `
  margin: 10px 20px;
  color: lightgray;
`

const ContentContainer = styled.div `
  margin-left: 20%;
  margin-right: 20%;
  margin-top: 20px;
  margin-bottom: 20px;
`
const Content = styled.div `
  color: black;
  size: large;
`
export const EntryMain: React.FC<RouteComponentProps<{slug: string}>> = (props: any) => {
  const slug = props.match.params.slug
  const { data, isLoading, hasError, handleRequest } = useGetHandler();
  console.log(slug)

  if (hasError){
    message.error("An error has occured. Please reload");

    return(
      <div>
        <SplashContainer/>
        <Spin/>
      </div>
    )
  }

  if (data === null && isLoading === false){
    slugRequest.url = "/"+slug+"/"
    console.log(slugRequest)
    handleRequest(slugRequest)
  }


  return (
    (data === null || isLoading) ?
    <div>
      <SplashContainer/>
      <Spin/>
    </div>
      :
    <div>
      <SplashContainer style={{backgroundSize:"cover", backgroundImage:"url("+data[0].photoLink+")"}}>
        <Title style={{color:"white", textShadow:"2px 0 0 #000, 0 -1px 0 #000, 0 1px 0 #000, -1px 0 0 #000"}}>
          {data[0].title}</Title>
      </SplashContainer>
      <Divider orientation="left">{data[0].timestamp}</Divider>
      <ContentContainer>
        <Content>{data[0].content}</Content>
      </ContentContainer>
    </div>
  )
}
/*
      <LeftFlex>
        <Timestamp>{data[0].timestamp}</Timestamp>
      </LeftFlex>

*/

export default EntryMain;
