import React, { } from 'react';
import { RouteComponentProps } from 'react-router-dom';
import styled from 'styled-components';
import useGetHandler from '../hooks/FlaskAPI';
import { Spin, message, Typography, Divider, Affix, Button, Tag } from 'antd'
import { RightOutlined } from "@ant-design/icons"

const ReactMarkdown = require('react-markdown')

const { Title } = Typography

const slugRequest = {
  method: 'get',
  url: '',
};

const SplashContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;

  height: 20vw;
  width: 100%;
`
const LeftFlex = styled.div`
  display: flex;
  justify-content: left;
  align-items: center;
`
const RightFlex = styled.div`
  display: flex;
  justify-content: right;
  align-items: center;
`

const Timestamp = styled.h5`
  margin: 10px 20px;
  color: lightgray;
`

const ContentContainer = styled.div`
  margin-left: 20%;
  margin-right: 20%;
  margin-top: 20px;
  margin-bottom: 20px;
`
export const Article: React.FC<RouteComponentProps<{ slug: string }>> = (props: any) => {
  const slug = props.match.params.slug
  const { data, isLoading, hasError, handleRequest } = useGetHandler();
  console.log(slug)

  if (hasError) {
    message.error("An error has occured. Please reload");

    return (
      <div>
        <SplashContainer />
        <Spin />
      </div>
    )
  }

  if (data === null && isLoading === false) {
    slugRequest.url = "/" + slug + "/"
    console.log(slugRequest)
    handleRequest(slugRequest)
  }


  const input = '# This is a header\n\nAnd this is a paragraph'
  return (
    (data === null || isLoading) ?
      <div>
        <SplashContainer>
          <Spin />
        </SplashContainer>
      </div>
      :
      <div>
        <SplashContainer style={{ backgroundSize: "cover", backgroundImage: "url(" + data[0].photoLink + ")" }}>
          <Title style={{ color: "white", textShadow: "2px 0 0 #000, 0 -1px 0 #000, 0 1px 0 #000, -1px 0 0 #000" }}>
            {data[0].title}</Title>
        </SplashContainer>
        <Divider orientation="left">{data[0].timestamp}</Divider>
        <ContentContainer>
          <ReactMarkdown source={data[0].content} />
        </ContentContainer>
        {
          (data[0].nextArticleLink !== "") ?
            <RightFlex>
              <Affix offsetBottom={10}>
                <Button target={data[0].nextArticleLink} type="primary" shape="round" icon={<RightOutlined />} size="large" />)
            </Affix>
            </RightFlex>
            :
            <div />
        }
        {
          (data[0].github !== "") ?
            <a style={{ backgroundImage: "url(github.png)", backgroundSize: "cover", width: "150px", height: "150px", marginLeft: "10px", marginRight: "10px" }} href={data[0].githubLink} />
            :
            <div />
        }
        {
          (data[0].tags !== "") ?
            data[0].tags.split(',').map((tag: string) => <Tag>tag</Tag>)
            :
            <div />
        }

      </div>
  )
}


export default Article;
