import * as React from 'react';
import styled from 'styled-components';
import { EntryTrunc } from '../data/Entry'
import { Typography } from 'antd'

const {Title} = Typography;


const Layout = styled.div`
  width: 90% auto;
  margin: 16px auto;
  padding: 16px;
  position: relative;
  min-width:280;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
`;

const ArticleFlex = styled.div `
  margin: 10px;
  flex-grow: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ArticleContainer = styled.div `
  height: 300px;
  width: 300px;
  background-image: url('placeholder.jpg');

  display: flex;
  justify-content: center;
  align-items: center;
`

const Article: React.FC<{
  entry: EntryTrunc
}> = (props) => {
  return (
    <ArticleFlex >
      <ArticleContainer>
        <h3>Hello</h3>
      </ArticleContainer>
    </ArticleFlex>
  )
}

const ArticleSummary: React.FC<{
  entries: EntryTrunc[]
}> = (props) => {
  return (
    <div style={{background:"#eee", marginTop:"10px"}}>
      <h1 style={{paddingTop:"20px"}}>Some of my recent projects</h1>
      <Layout>
        {props.entries.map(e => <Article entry={e} />)}
      </Layout>
    </div>
  );
};

export default ArticleSummary;
